function [IaN] = IaNModel( P,W,N_0,alpha,d1x,d1y,d2x,d2y,d3x,d3y)
        
    C_IaN_User3 = (W).*log2(1 + P./((d3x.^2 + d3y.^2).^(0.5.*alpha).*(N_0.*W + P./(3.46.*(0.0833.*d3y.^2 + (1.0 - 0.289.*d3x).^2).^0.5).^alpha + P./(2.0.*(0.25.*d3x.^2 + (-0.5.*d3y - 1.0).^2).^0.5).^alpha + P./(2.0.*(0.25.*d3x.^2 + (1.0 - 0.5.*d3y).^2).^0.5).^alpha + P./(1.73.*((1.0 - 0.577.*d3x).^2 + 0.333.*(-d3y - 1.0).^2).^0.5).^alpha + P./(1.73.*((1.0 - 0.577.*d3x).^2 + 0.333.*(1.0 - d3y).^2).^0.5).^alpha)));
    C_IaN_User2 = (W).*log2(1 + P./((d2x.^2 + d2y.^2).^(0.5.*alpha).*(N_0.*W + P./(3.0.*((1.0 - 0.333.*d2y).^2 + 0.333.*(-0.577.*d2x - 1.0).^2).^0.5).^alpha + P./(2.0.*(0.25.*d2x.^2 + (1.0 - 0.5.*d2y).^2).^0.5).^alpha + P./(1.73.*((-0.577.*d2x - 1.0).^2 + 0.333.*(-d2y - 1.0).^2).^0.5).^alpha + P./(1.73.*(0.333.*(1.0 - d2y).^2 + (-0.577.*d2x - 1.0).^2).^0.5).^alpha + P./(1.73.*((1.0 - 0.577.*d2x).^2 + 0.333.*(1.0 - d2y).^2).^0.5).^alpha)));
    C_IaN_User1 = (W).*log2(1 + P./((d1x.^2 + d1y.^2).^(0.5.*alpha).*(N_0.*W + P./(3.0.*(0.333.*(-0.577.*d1x - 1.0).^2 + (-0.333.*d1y - 1.0).^2).^0.5).^alpha + P./(2.0.*(0.25.*d1x.^2 + (-0.5.*d1y - 1.0).^2).^0.5).^alpha + P./(1.73.*((-0.577.*d1x - 1.0).^2 + 0.333.*(-d1y - 1.0).^2).^0.5).^alpha + P./(1.73.*(0.333.*(1.0 - d1y).^2 + (-0.577.*d1x - 1.0).^2).^0.5).^alpha + P./(1.73.*((1.0 - 0.577.*d1x).^2 + 0.333.*(-d1y - 1.0).^2).^0.5).^alpha)));
    IaN = C_IaN_User3+C_IaN_User2+C_IaN_User1;

end